import React, { useState } from 'react';
import LocationList from './Locations/LocationList';
import Map from './Map/Map';

import classes from './MapBlock.module.scss';


const MapBlock = () => {
  const [activeIndex, setActiveIndex] = useState(false);
  const [mapLocation, setMapLocation] = useState({
    lng: 49.39048,
    lat: 53.54167,
  });

  const locations = [
    {
      city: 'Тольятти',
      address: 'Автозаводское ш, 6',
      tel: 'тел 8 (848) 242-18-88',
      hours: '10:00 22:00',
      mapLocation: {
        lng: 49.39048,
        lat: 53.54167,
      },
    },
    {
      city: 'Тольятти',
      address: 'Революционная ул, 52А',
      tel: 'тел 8 (848) 242-18-88',
      hours: '10:00 22:00',
      mapLocation: {
        lng: 49.27154,
        lat: 53.51809,
      },
    },
    {
      city: 'Тольятти',
      address: 'Дзержинского ул, 21',
      tel: 'тел 8 (848) 242-18-88',
      hours: '0:00 22:00',
      mapLocation: {
        lng: 49.31947,
        lat: 53.53385,
      },
    },
  ];

  return (
    <div className={classes.MapBlock}>
      <h2 className={classes.title}>посетите наши магазины</h2>
      <div className={classes.Wrapper}>
      <ul className={classes.List}>
      {locations.map((value, i) => (
        <li
          key={i}
          onClick={() => clickHandler(i)}
          className={activeIndex === i ? `${classes.active}` : ''}
        >
          <span> {value.city}</span>
          <span> {value.address}</span>
          <span> {value.tel}</span>
          <span> {value.hours}</span>
        </li>
      ))}
    </ul>
        <Map mapLocation={mapLocation} />
      </div>
    </div>
  );
};

export default MapBlock;
