import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  categoryId: 0,
};

const filterSlice = createSlice({
  name: '',
  initialState,
  reducers: {
    setCategotyId(state,action) {
      state.categoryId = action.payload
    }
  },
});

export const {} = filterSlice.actions;

export default filterSlice.reducer;
