import { useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { selectSort, setSort } from '../../redux/slices/filterSlice';

import classes from './Sort.module.scss';

type SortItem = {
  name: string
  sortProperty: string
}

type PopupClick = MouseEvent & {
  path: Node[]
}

export const sortList: SortItem[] = [
  { name: 'популярности (убыванию)', sortProperty: 'rating' },
  { name: 'популярности (возрастанию)', sortProperty: '-rating' },
  { name: 'цене (убыванию)', sortProperty: 'price' },
  { name: 'цене (возрастанию)', sortProperty: '-price' },
  { name: 'алфавиту (убыванию)', sortProperty: 'title' },
  { name: 'алфавиту (возрастанию)', sortProperty: '-title' },
];

const Sort = () => {
  const dispatch = useDispatch();
  const sort = useSelector(selectSort);
  const sortRef = useRef<HTMLDivElement>(null);

  const [open, setOpen] = useState(false);

  const onClickListItem = (obj: SortItem) => {
    dispatch(setSort(obj));
    setOpen(false);
  };

  // useEffect(() => {
  //   const handleClickOutside = (event: MouseEvent) => {
  //     const _event = event as PopupClick;
  //     if (sortRef.current && !_event.path.includes(sortRef.current)) {
  //       setOpen(false);
  //     }
  //   };
  //   document.body.addEventListener('click', handleClickOutside);
  //   return () => document.body.removeEventListener('click', handleClickOutside);
  // }, []);

  return (
    <div ref={sortRef} className={classes.Sort}>
      <div className={classes.Label}>
        <b>Сортировка по: </b>
        <div onClick={() => setOpen(!open)}>
          <span>{sort.name}</span>
          <svg
            width="10"
            height="6"
            viewBox="0 0 10 6"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M10 5C10 5.16927 9.93815 5.31576 9.81445 5.43945C9.69075 5.56315 9.54427 5.625 9.375 5.625H0.625C0.455729 5.625 0.309245 5.56315 0.185547 5.43945C0.061849 5.31576 0 5.16927 0 5C0 4.83073 0.061849 4.68424 0.185547 4.56055L4.56055 0.185547C4.68424 0.061849 4.83073 0 5 0C5.16927 0 5.31576 0.061849 5.43945 0.185547L9.81445 4.56055C9.93815 4.68424 10 4.83073 10 5Z"
              fill="#2C2C2C"
            />
          </svg>
        </div>
      </div>
      {open && (
        <div className={classes.Popup}>
          <ul>
            {sortList.map((obj, i) => (
              <li
                key={i}
                onClick={() => onClickListItem(obj)}
                className={
                  sort.sortProperty === obj.sortProperty
                    ? `${classes.active}`
                    : ''
                }
              >
                {obj.name}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Sort;
