import React from 'react';
// import axios from 'axios';
// import { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import second from '../redux/slices/'

import classes from './MobileData.module.scss';
import Loading from '../components/ui/Loader/loader';
import { useDispatch, useSelector } from 'react-redux';

const MobileData = () => {
  // const [mobile, setMobile] = useState();
  const { items, status } = useSelector((state) => state.mobileData);
  const { id } = useParams();
  const dispatch = useDispatch();

  const getMobileData = async () => {
    dispatch(
      fetchMobileValue(id)
    );
  };

  useEffect(() => {
    window.scrollTo(0, 0);
    getMobileData();
    isSearch.current = false;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // useEffect(() => {
  //   async function fetchMobile() {
  //     try {
  //       const { data } = await axios.get(
  //         `https://6363c19237f2167d6f828690.mockapi.io/items/` + id
  //       );
  //       setMobile(data);
  //     } catch (error) {
  //       alert('error');
  //     }
  //   }
  //   fetchMobile();
  //   // eslint-disable-next-line react-hooks/exhaustive-deps
  // }, []);

  // if (!items) {
  //   return <Loading/>;
  // }

  return (
    <div className={classes.content}>
      <img className={classes.img} src={items.imageUrl} alt="" />
      <div className={classes.textData}>
        <h2>{items.title}</h2>
        <p>характеристики:</p>
        <span>Объем памяти: {items.types}</span>
        <span>цвет: {items.sizes}</span>
        <span>цена: {items.price} руб</span>
        {status === 'error' ? (
          <div className={classes.Error}>
            <h2>что-то пошло не так((</h2>
            <span>попробуйте повторить попытку позже</span>
          </div>
        ) : (
          <div className={classes.Items}>
            {status === 'loading' ? <Loading /> : items}
          </div>
        )}
      </div>
    </div>
  );
};

export default MobileData;
