import React from 'react';
import qs from 'qs';
import { useNavigate } from 'react-router-dom';
import { useEffect, useContext, useRef, useCallback } from 'react';

import { SearchContext } from '../App';

import {
  setCategotyId,
  setCurrentPage,
  setFliters,
} from '../redux/slices/filterSlice';
import { useDispatch, useSelector } from 'react-redux';

import Sort, { sortList } from '../components/Sort/Sort';
import Categories from '../components/Categories/Categories';
import Skeleton from '../components/PizzaBlock/Skeleton/Skeleton';
import PizzaBlock from '../components/PizzaBlock/PizzaBlock';
import Pagination from '../components/Pagination';

import classes from '../scss/app.module.scss';
import { fetchMobile } from '../redux/slices/mobileSlice';

const Home = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const isSearch = useRef(false);
  const isMounted = useRef(false);
  const { items, status } = useSelector((state) => state.mobile);

  const { categoryId, sort, currentPage } = useSelector(
    (state) => state.filter
  );
  const sortType = sort.sortProperty;

  const { searchValue } = useContext(SearchContext);

  const onChangeCategory = useCallback((idx) => {
    dispatch(setCategotyId(idx));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const onChangePage = (page) => {
    dispatch(setCurrentPage(page));
  };

  const пуе = () => {
    setIsLoading(true);

    const sortBy = sortType.replace('-', '');
    const order = sortType.includes('-') ? 'возрастанию' : 'убыванию';
    const category = categoryId > 0 ? `category=${categoryId}` : '';
    const search = searchValue ? `&search=${searchValue}` : '';

    axios
      .get(
        `https://6363c19237f2167d6f828690.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`
      )
      .then((res) => {
        setItems(res.data);
        setIsLoading(false);
      });
  };

  useEffect(() => {
    if (isMounted.current) {
      const queryString = qs.stringify({
        sortProperty: sort.sortProperty,
        categoryId,
        currentPage,
      });

      navigate(`?${queryString}`);
    }
    isMounted.current = true;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [categoryId, sort.sortProperty, currentPage]);

  useEffect(() => {
    if (window.location.search) {
      const params = qs.parse(window.location.search.substring(1));

      const sort = sortList.find(
        (obj) => obj.sortProperty === params.sortProperty
      );

      dispatch(
        setFliters({
          ...params,
          sort,
        })
      );
      isSearch.current = true;
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    window.scrollTo(0, 0);

    getMobile();

    isSearch.current = false;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [categoryId, sort.sortProperty, searchValue, currentPage]);

  const skeleton = [...new Array(4)].map((_, index) => (
    <Skeleton key={index} />
  ));
  const mobileBlock = items.map((obj) => <PizzaBlock key={obj.id} {...obj} />);

  return (
    <div>
      <div className={classes.Top}>
        <Categories value={categoryId} onChangeCategory={onChangeCategory} />
        <Sort />
      </div>
      <h2 className={classes.Title}>Все смартфоны</h2>
      {status === 'error' ? (
        <div>
          <span>попробуйте повторить попытку позже</span>
        </div>
      ) : (
        <div className={classes.Items}>
          {status === 'loading' ? skeleton : mobileBlock}
        </div>
      )}
      <Pagination currentPage={currentPage} onChangePage={onChangePage} />
    </div>
  );
};

export default Home;
