import React from 'react';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addItem } from '../../redux/slices/cartSlice';

import '../ui/Button/Button';
import classes from './PizzaBlock.module.scss';

const typeNames = ['128 GB', '512 GB'];

const PizzaBlock = ({ id, title, price, imageUrl, types, sizes }) => {
  const dispatch = useDispatch();
  const cartItem = useSelector((state) =>
    state.cart.items.find((obj) => obj.id === id)
  );
  const [activeType, setActiveType] = useState(0);
  const [activeSize, setActiveSize] = useState(0);

  const addedCount = cartItem ? cartItem.count : 0;

  const onClickAdd = () => {
    const item = {
      id,
      title,
      price,
      imageUrl,
      type: typeNames[activeType],
      size: sizes[activeSize],
    };
    dispatch(addItem(item));
  };

  return (
    <div className={classes.PizzaBlockWrapper}>
      <div className={classes.PizzaBlock}>
        <img className={classes.Image} src={imageUrl} alt="Pizza" />
        <h4 className={classes.Title}>{title}</h4>
        <div className={classes.Selector}>
          <ul>
            {types.map((value, i) => (
              <li
                key={i}
                onClick={() => setActiveType(i)}
                className={activeType === i ? `${classes.active}` : ''}
              >
                {value}
              </li>
            ))}
          </ul>
          <ul>
            {sizes.map((value, i) => (
              <li
                key={i}
                onClick={() => setActiveSize(i)}
                className={activeSize === i ? `${classes.active}` : ''}
              >
                {value}
              </li>
            ))}
          </ul>
        </div>
        <div className={classes.Bottom}>
          <div className={classes.Price}>от {price} руб</div>
          <button
            onClick={onClickAdd}
            className="button button--outline button--add"
          >
            <svg
              width="12"
              height="12"
              viewBox="0 0 12 12"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
                fill="white"
              />
            </svg>
            <span>Добавить</span>
            {addedCount > 0 && <i>{addedCount}</i>}
          </button>
        </div>
      </div>
    </div>
  );
};

export default PizzaBlock;
