import { createSlice } from '@reduxjs/toolkit';

const locations = [
  {
    city: 'Тольятти',
    address: 'Автозаводское ш, 6, 1 Этаж',
    tel: 'тел 8 (848) 242-18-88',
    hours: '10:00 22:00',
    mapLocation: {
      lng: 49.39024,
      lat: 53.54194,
    },
  },

  {
    city: 'Тольятти',
    address: 'Революционная ул, 52А, 2 Этаж',
    tel: 'тел 8 (848) 242-18-88',
    hours: '10:00 22:00',
    mapLocation: {
      lng: 49.27154,
      lat: 53.51809,
    },
  },

  {
    city: 'Тольятти',
    address: 'Дзержинского ул, 21, 3 Этаж',
    tel: 'тел 8 (848) 242-18-88',
    hours: '10:00 22:00',
    mapLocation: {
      lng: 49.31947,
      lat: 53.53385,
    },
  },
];

const initialState = {
  mapLocation: {
    lat: 53.54167,
    lng: 49.39048,
  },
  active: 0,
};

const mapSlice = createSlice({
  name: 'map',
  initialState,
  reducers: {
    setMapLocation(state, action) {
      state.mapLocation = action.payload.locations;
    },
    setActive(state, action) {
      state.active = action.payload;
    },
  },
});

export const { setMapLocation, setActive } = mapSlice.actions;

export default mapSlice.reducer;
