import React, { useState } from 'react';
import LocationList from './Locations/LocationList';
import Map from './Map/Map';

import classes from './MapBlock.module.scss';


const MapBlock = () => {
  const [mapLocation, setMapLocation] = useState({
    lng: 49.39048,
    lat: 53.54167,
  });

  return (
    <div className={classes.MapBlock}>
      <h2 className={classes.title}>посетите наши магазины</h2>
      <div className={classes.Wrapper}>
      <ul className={classes.List}>
      {locations.map((value, i) => (
        <li
          key={i}
          onClick={() => clickHandler(i)}
          className={activeIndex === i ? `${classes.active}` : ''}
        >
          <span> {value.city}</span>
          <span> {value.address}</span>
          <span> {value.tel}</span>
          <span> {value.hours}</span>
        </li>
      ))}
    </ul>
        <Map mapLocation={mapLocation} />
      </div>
    </div>
  );
};

export default MapBlock;
