import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  items: [],
};

const mobileSlice = createSlice({
  name: 'mobile',
  initialState,
  reducers: {
    setMobile(state, action) {
      state.items = action.payload;
    },
  },
});

export const { setMobile } = mobileSlice.actions;

export default mobileSlice.reducer;
