import classes from './LocationList.module.scss';

const LocationList = (props) => {
  const [active, setActive] = useState(false);

  const clickHandler = (data) => {
    locationHandler(data.mapLocation);
    setActive(!active);
  };

  const locations = [
    {
      city: 'Тольятти',
      address: 'Автозаводское ш, 6',
      tel: 'тел 8 (848) 242-18-88',
      hours: '10:00 22:00',
      mapLocation: {
        lng: 49.39048,
        lat: 53.54167,
      },
    },

    {
      city: 'Тольятти',
      address: 'Революционная ул, 52А',
      tel: 'тел 8 (848) 242-18-88',
      hours: '10:00 22:00',
      mapLocation: {
        lng: 49.27154,
        lat: 53.51809,
      },
    },

    {
      city: 'Тольятти',
      address: 'Дзержинского ул, 21',
      tel: 'тел 8 (848) 242-18-88',
      hours: '10:00 22:00',
      mapLocation: {
        lng: 49.31947,
        lat: 53.53385,
      },
    },
  ];

  return (
    <ul className={classes.List}>
      {locations.map((data, id) => (
        <li key={id} onClick={() => clickHandler(data)}>
          <h3 className={classes.Title}>{data.city}</h3>
          <span className={classes.itemAddress}>{data.address}</span>
          <span className={classes.Phone}>{data.Phone}</span>
          <span className={classes.Hours}>{data.hours}</span>
        </li>
      ))}
    </ul>
  );
};

export default LocationList;
