      }
    },
    removeItem(state, action) {
      state.items = state.items.filter((obj) => obj.id !== action.payload);
    },
    clearitem(state) {
      state.items = [];
    },
  },
});

export const { addItem, removeItem, clearitem } = cartSlice.actions;

export default cartSlice.reducer;
