import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchMobileValue = createAsyncThunk(
  'mobile/fetchMobilesStatus',
  async (id) => {
    const { data } = await axios.get(
      `https://6363c19237f2167d6f828690.mockapi.io/items` + id
    );
    return data;
  }
);

const initialState = {
  items: [],
  status: 'loading',
};

const mobileDataSlice = createSlice({
  name: 'mobileValue',
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload;
    },
  },
  extraReducers: {
    [fetchMobileValue.pending]: (state) => {
      state.status = 'loading';
      state.items = [];
    },
    [fetchMobileValue.fulfilled]: (state, action) => {
      state.status = 'success';
      state.items = action.payload;
    },
    [fetchMobileValue.rejected]: (state) => {
      state.status = 'error';
      state.items = [];
    },
  },
});

export const { setItems } = mobileDataSlice.actions;

export default mobileDataSlice.reducer;
