import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchMobile = createAsyncThunk('mobile/fetchMobilesStatus', async (params) => {
  const {    sortBy,
    order,
    category,
    search} =params
  const { data } = await axios.get(
    `https://6363c19237f2167d6f828690.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`
  );
  return data
});

const initialState = {
  items: [],
};

const mobileSlice = createSlice({
  name: 'mobile',
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload;
    },
  },
});

export const { setItems } = mobileSlice.actions;

export default mobileSlice.reducer;
