import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  mapLocation: {
    lat: 53.54167,
    lng: 49.39048,
  },
  status: 'loading',
};

const mapSlice = createSlice({
  name: 'map',
  initialState,
  reducers: {
    setMapLocation(state, actions) {
      state.mapLocation = actions.payload;
    },
  },
});

export const setMap = (state) => state.mobile;

export const { setItems } = mapSlice.actions;

export default mapSlice.reducer;
